mapper.select:
		select media_no, media_name, media_data, media_type, media_from, from_no
  		  from media
  	 	 where media_from = #{media_from}
  	 	   and from_no = #{from_no}

		select media_no, media_name, media_data, media_type, media_from, from_no
  		  from media
  	 	 where media_no = #{media_no}
